mapper.select:
select
  r.id,
  d.name AS departmentName,
  e.actual_name AS employeeName
from vis_department_reviewer r
LEFT JOIN t_department d ON r.department_id = d.id
LEFT JOIN t_employee e ON r.employee_id = e.id


        AND r.id = #{queryDTO.id}


        AND r.department_id = #{queryDTO.departmentId}


        AND r.employee_id = #{queryDTO.employeeId}



        AND r.create_time >= #{queryDTO.createTimeBegin}


        AND r.create_time <= #{queryDTO.createTimeEnd}



        AND r.update_time >= #{queryDTO.updateTimeBegin}


        AND r.update_time <= #{queryDTO.updateTimeEnd}

select
   id,
   department_id,
   employee_id
from vis_department_reviewer


        AND id = #{queryDTO.id}


        AND department_id = #{queryDTO.departmentId}


        AND employee_id = #{queryDTO.employeeId}



        AND create_time >= #{queryDTO.createTimeBegin}


        AND create_time <= #{queryDTO.createTimeEnd}



        AND update_time >= #{queryDTO.updateTimeBegin}


        AND update_time <= #{queryDTO.updateTimeEnd}
