mapper.select:
select
    ep.id,
    ep.community_id,
    ec.name communityName,
    ep.name,
    ep.remark,
    ep.create_time,
    ep.update_time
from e_plot ep
left join e_community ec on ep.community_id = ec.id


        AND ep.id = #{queryDTO.id}


        AND ep.community_id = #{queryDTO.communityId}


        AND INSTR(ep.name,#{queryDTO.name})



        AND ep.create_time >= #{queryDTO.createTimeBegin}


        AND ep.create_time <= #{queryDTO.createTimeEnd}



        AND ep.update_time >= #{queryDTO.updateTimeBegin}


        AND ep.update_time <= #{queryDTO.updateTimeEnd}

select
   id,
   community_id,
   name,
   remark,
   create_time,
   update_time
from e_plot


        AND id = #{queryDTO.id}


        AND INSTR(name,#{queryDTO.name})



        AND create_time >= #{queryDTO.createTimeBegin}


        AND create_time <= #{queryDTO.createTimeEnd}



        AND update_time >= #{queryDTO.updateTimeBegin}


        AND update_time <= #{queryDTO.updateTimeEnd}
