mapper.select:
select d.id, d.name, d.short_name, d.parent_id, d.sort, d.update_time, d.create_time, d.code, d.eng_name, e.actual_name AS managerName FROM t_department d LEFT JOIN t_employee e ON d.manager_id=e.id
AND d.id = #{queryDTO.id}
AND INSTR(d.name,#{queryDTO.name})
AND INSTR(d.short_name,#{queryDTO.shortName})
AND d.manager_id = #{queryDTO.managerId}
AND d.code = #{queryDTO.code}
AND INSTR(d.engName,#{queryDTO.engname})
AND d.create_time >= #{queryDTO.createTimeBegin}
AND d.create_time <= #{queryDTO.createTimeEnd}
AND d.update_time >= #{queryDTO.updateTimeBegin}
AND d.update_time <= #{queryDTO.updateTimeEnd}
order by d.code asc

select
id,
name,
short_name,
manager_id,
parent_id,
sort,
update_time,
create_time,
code,
eng_name
from t_department


        AND id = #{queryDTO.id}


        AND INSTR(name,#{queryDTO.name})


        AND INSTR(short_name,#{queryDTO.shortName})


        AND manager_id = #{queryDTO.managerId}


        AND code = #{queryDTO.code}


        AND INSTR(engName,#{queryDTO.engname})



        AND create_time >= #{queryDTO.createTimeBegin}


        AND create_time <= #{queryDTO.createTimeEnd}



        AND update_time >= #{queryDTO.updateTimeBegin}


        AND update_time <= #{queryDTO.updateTimeEnd}
