mapper.select:
select id, login_name, login_pwd, actual_name, nick_name, phone, id_card, birthday, email, department_id, is_leave, is_disabled, remark, create_user, update_time, create_time, is_delete, identity from t_user
AND login_name = #{queryDTO.loginName}
AND INSTR(login_pwd,#{queryDTO.loginPwd})
AND create_time >= #{queryDTO.createTimeBegin}
AND create_time <= #{queryDTO.createTimeEnd}
AND update_time >= #{queryDTO.updateTimeBegin}
AND update_time <= #{queryDTO.updateTimeEnd}

